sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

	var ngr=new GlideRecord("x_624695_librarym_user");
	ngr.initialize();
	ngr.addQuery('user_id',current.user_id);
	ngr.query();
	if(ngr.next())
		{
			if(current.user_id==ngr.user_id)
				{
					gs.info("Found the record::"+ngr.user_id);
					ngr.no_of_books=ngr.no_of_books-1;
					if(ngr.no_of_books<=0)
						{
							ngr.no_of_books=0;
						}
				}
		}
    var gr = new GlideRecord("x_624695_librarym_issue_books");
    gr.initialize();
    gr.addQuery('user_id', current.user_id);
    gr.query();
    if (gr.next()) {
        if (current.user_id == gr.user_id) {
			gs.info("Found::"+gr.user_id);
            gr.bookcount = gr.bookcount - 1;
            gs.info("Current Book Count in issue ::" + gr.bookcount);
        }
    }
	ngr.update();
    gr.update();
	ngr.execute();
    gr.execute();

})(current, previous);
