sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	var val=current.returndate;
	var parts =val.split('-');
	var date1 = new Date(parts[0], parts[1] - 1, parts[2]); 
	var val2=current.deadline;
	var parts2 =val2.split('-');
	var date2 = new Date(parts2[0], parts2[1] - 1, parts2[2]); 
	var diffTime = Math.abs(date2 - date1);
	var diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 
    if (current.remarks == "pagestorn") {
        current.totalfine = 10.0;
    } else if (current.remarks == "PagesMissing") {
        current.totalfine = 15.0;
    } else {
        current.totalfine = 20.0;
    }
	gs.info("Current Value in remarks field is ::"+current.remarks);
	current.Datediff=diffDays;
	gs.info("Value in datediff is ::"+current.Datediff);
	current.total_fine=current.total_fine+current.Datediff;
	gs.info("The total fine value is ::"+current.totalfine);
})(current, previous);
