sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Add your code here
    var gr = new GlideRecord("x_624695_librarym_user");
    gr.initialize();
    gr.addQuery('user_id', current.user_id);
    gr.query();
	var obj=new GlideRecord("x_624695_librarym_book");
	obj.initialize();
	obj.addQuery('number',current.book_id.getDisplayValue());
	obj.query();
    if (gr.next()) {
        if (current.user_id == gr.user_id) {
            current.bookcount = gr.no_of_books + 1;
            if (current.bookcount <= 3 || obj.book_count<=0) {
                gr.no_of_books = gr.no_of_books + 1;
                gs.info("Current Book Count with gr in incr::" + gr.no_of_books);
            } else {
                current.bookcount = gr.no_of_books - 1;
                current.setAbortAction(true);
            }
        }
    }
	if(obj.next())
		{
			if(obj.number==current.book_id.getDisplayValue())
				{
					obj.book_count=obj.book_count-1;
				}
		}
    gr.update();
	obj.update();
    gr.execute();
	obj.execute();
})(current, previous);
